import { Component, Input, OnChanges, OnInit, SimpleChanges } from '@angular/core';
import { Item } from 'src/app/interfaces/item';
import { ShoppingListService } from 'src/app/service/shopping-list.service';

@Component({
  selector: 'app-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.css']
})
export class InputComponent implements OnInit, OnChanges {
  @Input() editingItem!: Item

  itemName!: string

  constructor(
    private shoppingListService: ShoppingListService
  ) { }

  ngOnChanges(changes: SimpleChanges): void {
    if (!changes['editingItem'].firstChange) {
      this.itemName = this.editingItem?.name
    }
  }

  ngOnInit(): void { }

  addItem() {
    this.shoppingListService.addItemToShoppingList(this.itemName)
    this.clear()
  }

  clear(){
    this.itemName = ''
  }
}
<form class="add-item" (ngSubmit)="addItem()">
  <input type="text" name="item" placeholder="Digite o item que deseja adicionar" [(ngModel)]="itemName">
  <button type="submit">Salvar item</button>
</form>
